<?phpclass Configurationmanager extends CI_Controller {    function __construct() {        parent::__construct();        $this->is_logged_in();    }    function configurationform() {        $this->load->model('configuration');        $configuration = new Configuration();        $data['configuration'] = $configuration->get_by_id(1);        $data['main_content'] = 'backend/configuration/configurationform_view';        $this->load->view('backend/includes/template', $data);    }    function saveconfiguration() {        $this->load->library('form_validation');        $this->form_validation->set_rules('keywords', 'keywords', 'trim|required');        $this->form_validation->set_rules('email', 'email', 'trim|required|valid_email');        $this->form_validation->set_rules('password', 'password', 'trim|required');        if ($this->form_validation->run() == false) {            $this->configurationform();        } else {            $this->load->model('configuration');            $configuration = new Configuration();            $configuration->keywords = $this->input->post('keywords');            $configuration->email = $this->input->post('email');            $configuration->facebook = $this->input->post('facebook');            $configuration->twitter = $this->input->post('twitter');            $configuration->sitemap = $this->input->post('sitemap');            $configuration->receiver_mail_1 = $this->input->post('receiver_mail_1');            $configuration->receiver_mail_2 = $this->input->post('receiver_mail_2');            $configuration->receiver_mail_3 = $this->input->post('receiver_mail_3');            $configuration->password = $this->input->post('password');            $configuration->id = 1;            if ($configuration->save()) {                redirect('backend/home/backend', 'refresh');            }        }    }    function is_logged_in() {        $is_logged_in = $this->session->userdata('is_logged_in');        if (!isset($is_logged_in) || $is_logged_in != true) {            redirect("backend/login/accessdenaied");            die();        }    }}?>