<?phpclass Login extends CI_Controller {    function __construct() {        parent::__construct();        $this->output->set_header('Last-Modified: ' . gmdate("D, d M Y H:i:s") . ' GMT');        $this->output->set_header('Cache-Control: no-store, no-cache, must-revalidate, post-check=0, pre-check=0');        $this->output->set_header('Pragma: no-cache');        $this->output->set_header("Expires: Mon, 26 Jul 1997 05:00:00 GMT");    }    function index() {        $data['main_content'] = 'backend/login/login_view';        $this->load->view('backend/includes/template', $data);    }    function logout() {        //$this->session->all_userdata();//$this->session->all_userdata()        $this->session->unset_userdata($this->session->all_userdata());        $this->session->sess_destroy();        $data['main_content'] = 'backend/login/login_view';        $this->load->view('backend/includes/template', $data);    }    function validate() {        $this->load->model('User');        $user = new User();        $user->where('email', $this->input->post('email'));        $user->where('password', md5($this->input->post('password')));        $q = $user->get();        if ($q->id) {            $data = array(                'username' => $this->input->post('email'),                'is_logged_in' => true            );            $this->session->set_userdata($data);            redirect("backend/home/backend");        } else {            $this->index();        }    }    function accessdenaied() {        $data['main_content'] = 'backend/login/accessdenaied_view';        $this->load->view('backend/includes/template', $data);    }}?>