<?phpinclude_once 'mail/Mailer.php';class Home extends CI_Controller {    function __construct() {        parent::__construct();        $this->load->model('configuration');        $this->load->helper('language');    }    function index() {        $this->load->view('frontend/includes/template');    }    private function sendmail() {        $mail = new Mailer();        $this->load->model('Configuration');        $configuration = new Configuration();        $conf = $configuration->get();        $sentmail = $conf->email;        $sentpassword = $conf->password;        if (!empty($conf->receiver_mail_1))            $data[] = $conf->receiver_mail_1;        if (!empty($conf->receiver_mail_2))            $data[] = $conf->receiver_mail_2;        if (!empty($conf->receiver_mail_3))            $data[] = $conf->receiver_mail_3;        $subject = $this->input->post('subject');        $clientMail = $this->input->post('clientMail');        $message = $this->input->post('message');        $mail->SendTaskMail($data, $subject, $sentmail,  $clientMail . ' has sent the following :<br/>' . $message, $sentmail, $sentpassword);        redirect($this->input->post('url') . '/1');    }}?>