<?phpinclude_once dirname(__FILE__) . '/../AdminGenericController.php';class Home extends AdminGenericController {    function __construct() {        parent::__construct(true, array("judge","super_judge"));        $this->is_logged_in($this->session->userdata('usertype'));    }    function index() {        $data['main_content'] = 'frontend/judge/home_view';        $this->load->view('frontend/includes/template', $data);    }    function projects() {        $aColumns = array("id", "name", "team_leader_name", "school", "adult_sponsor_name");        $searchBy = array();        $model = new Project();        $orderCal = "id";        return $this->prepareTable($aColumns, $searchBy, $model, $orderCal);    }    protected function formatData($modelsResult) {        $data = array();        foreach ($modelsResult as $item) {            $item_data = array();            $item_data[] = $item->name;            $item_data[] = $item->team_leader_name;            $item_data[] = $item->school;            $item_data[] = $item->adult_sponsor_name;            $item_data[] = date('Y-m-d ', strtotime($item->start_date));            $item_data[] = date('Y-m-d ', strtotime($item->end_date));            $item_data[] = $item->id;            $item_data[] = $item->id;            $data[] = $item_data;        }        return $data;    }}?>