nu = new Content();                        $menu->where('status', 'ACTIVE');                        $menu->where('parent', NULL);                        $menu->where('language_id', $laguageObject->id);                        $menu->where('header_menu', 1);                        $menu->order_by('content_order');                        $items = $menu->get();                        foreach ($items as $item) {                            ?>                            <li >                                <a href="<?php echo base_url() . $this->lang->lang() . '/' . $item->url . $item->id; ?>" class="topc_link"><?php echo $item->name; ?></a>                            </li><?php } ?>                    </ul>                </div>                <div class="logo"><a href="<?php echo base_url() . $laguageObject->code; ?>"><img src="<?php echo base_url(); ?>assets/frontend/images/logo.png" width="153" height="86" /></a></div>                <div class="lang-contaner"><div class="lang ar"><a id="ar"class="langClass" href="<?php echo base_url() . 'ar'; ?>">عربى</a></div><div class="lang ">|</div><div class="lang last"><a href="<?php echo base_url() . 'en'; ?>" class="langClass" id="en">English</a></div></div>                <script type="text/javascript">                    $("#<?php echo $laguageObject->code; ?>").addClass("current");                </script>            </div>            <!--header end-->            <!--  ♦ close Menu ♦  --> 